import React from 'react'


export default class Question extends React.Component {
    handleClick = (event) => {
        console.log(`answer: ${this.props.question.answer.toString()}`)
        console.log(`answerPressed: ${event.target.length}`)
        console.log("***************")
        this.props.handleClick(this.props.question.answer.toString() === event.target.value.toLowerCase())
    }
    render() {
        return (
            <div className="question">
                <p>{this.props.question.text}</p>
                <button onClick={this.handleClick}>True</button> <button onClick={this.handleClick}>False</button>
            </div>
        )
    }
}
